package Operators;

public class Unary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=10;
int b=++a;

int x=10;
int c=x++;

int h=10;
int v=--h;

int l=10;
int n=l--;

System.out.println(b);
System.out.println(c);
System.out.println(v);
System.out.println(n);	}

}
